mapper.insert:
INSERT INTO submission(
problem_id, user_id, status, language, code
)VALUES(
#{problemId}, #{userId}, #{status}, #{language}, #{code}
)

mapper.update:
UPDATE submission
SET status = #{status}
WHERE id = #{id}

mapper.select:
SELECT
id, problem_id, user_id, status, language
FROM submission
WHERE user_id = #{userId}
LIMIT #{col}, #{limit}

SELECT
id, problem_id, user_id, status, language
FROM submission
WHERE problem_id = #{problemId}
LIMIT #{col}, #{limit}
